import { useNavigation, useRoute } from "@react-navigation/native";
import React from "react";
import { Button, Text, View } from "react-native";
import { RadioButton } from "react-native-paper";
import { useFindAllQuestions } from "./questions.graphql";

export const Question: React.FC = () => {
  const { data } = useFindAllQuestions();
  const questions = data?.questions || [];

  const [checked, setChecked] = React.useState("first");

  const router = useRoute();
  const navigation = useNavigation();
  const { number } = router.params as { number: number };

  // {questions.map((question: any) => (
  //   <Text key={question.id}>{question.question}</Text>
  // ))}
  return (
    <View>
      <Text>Question {number}</Text>
      <View>
        <Text>First</Text>
        <RadioButton
          value="first"
          status={checked === "first" ? "checked" : "unchecked"}
          onPress={() => setChecked("first")}
        />
        <RadioButton
          value="second"
          status={checked === "second" ? "checked" : "unchecked"}
          onPress={() => setChecked("second")}
        />
      </View>
      <Button title="wroc" onPress={() => navigation.navigate("Home")} />
    </View>
  );
};
